// make a doubly-linked list
var categoryLinkedList = {};
var ll = categoryLinkedList; // for shorthand
categories.categories.map(function(c) {
  this_cat = ll[c.id] = ll[c.id] || {
    children: [],
    parents: []
  };

  this_cat.name = c.name;
  this_cat.shortName = c.shortName;
  this_cat.id = c.id;

  var parent = ll[c.parentId] = ll[c.parentId] || {
    children: [],
    parents: []
  };

  this_cat.parents.push(parent);
  parent.children.push(this_cat);
});

var scrape = job('scrape-shopstyle', function(data, done) {
  console.log(data);
  var url_template = 'http://www.shopstyle.com/api/v2/products?cat=$CAT&device=desktop&includeCategoryHistogram=true&includeProducts=true&limit=40&locales=all&offset=$OFFSET&pid=shopstyle&productScore=LessPopularityEpcJob&sort=Popular&track=false&view=angular';
  var scrapeUrl = function(cat, offset) {
    request.get({
      url: url_template.replace('$CAT', cat).replace('$OFFSET', offset),
      json: true
    }, function(e, r, b) {
      kip.fatal(e);
      b.products.map(function(product) {
        var sample = {
          name: product.name,
          description: product.description,
          tags: product.categories.map(function(c) { return c.id }),
          images: [product.image.sizes.Best.url],
          price: product.price,
          source: 'shopstyle',
          meta: {
            url: product.clickUrl
          }
        };
        sample = new db.TrainingData(sample);
        sample.save(function(e, s) {
          kip.fatal(e);
          console.log('saved', s.name);
        })
      })
      if (offset < 80) {
        scrapeUrl(cat, offset + 40)
      }
    });
  }
  scrapeUrl(data.id, 0);
  done();
})

var depthFirst = function(node) {
  if (node.children.length === 0) {
    console.log('scraaaaaape', node.id);
    scrape({
      id: node.id,
      name: node.name,
      shortName: node.shortName
    })
  } else {
    node.children.map(function(c) {
      depthFirst(c);
    })
  }
}


// You can run it with rebuild
// node scrape rebuild
if (process.argv[2] === 'rebuild') {
  console.log('rebuilding shopstyle'.green)
  depthFirst(categoryLinkedList['womens-clothes']);
  depthFirst(categoryLinkedList['handbags']);
  depthFirst(categoryLinkedList['womens-shoes']);
  depthFirst(categoryLinkedList['mens-clothes']);
  depthFirst(categoryLinkedList['mens-bags']);
  depthFirst(categoryLinkedList['mens-shoes']);
} else {
  console.log('running the scraper job'.green);
}
